// Generated by Selenium IDE
const { Builder, By, Key, until } = require('selenium-webdriver')
const assert = require('assert')

describe('TestUITriangulo', function() {
  this.timeout(30000)
  let driver
  let vars
  beforeEach(async function() {
    driver = await new Builder().forBrowser('chrome').build()
    vars = {}
  })
  afterEach(async function() {
    await driver.quit();
  })
  it('TestUITriangulo', async function() {
    await driver.get("http://localhost:3000/")
    await driver.manage().window().setRect({ width: 1053, height: 807 })
    await driver.findElement(By.linkText("Triangulo")).click()
    await driver.findElement(By.id("lado1")).click()
    await driver.findElement(By.id("lado1")).sendKeys("3")
    await driver.findElement(By.id("lado2")).click()
    await driver.findElement(By.id("lado2")).sendKeys("4")
    await driver.findElement(By.id("lado3")).click()
    await driver.findElement(By.id("lado3")).sendKeys("5")
    await driver.findElement(By.id("button")).click()
    assert.equal(await driver.findElement(By.id("area")).getAttribute('value'),"Area: 6")
    assert.equal(await driver.findElement(By.id("Perimetro")).getAttribute('value'),"Perimetro: 12")
    await driver.findElement(By.id("lado1")).clear()
    await driver.findElement(By.id("lado1")).click()
    await driver.findElement(By.id("lado1")).sendKeys("-3")
    await driver.findElement(By.id("button")).click()
    assert.equal(await driver.findElement(By.id("area")).getAttribute('value'),"No hay area porque hay valores negativos")
    assert.equal(await driver.findElement(By.id("Perimetro")).getAttribute('value'),"No hay perimetro porque hay valores negativos")
    await driver.findElement(By.id("lado1")).clear()
    await driver.findElement(By.id("lado2")).clear()
    await driver.findElement(By.id("lado3")).clear()
    await driver.findElement(By.id("lado1")).click()
    await driver.findElement(By.id("lado1")).sendKeys("1e301")
    await driver.findElement(By.id("lado3")).click()
    await driver.findElement(By.id("lado3")).sendKeys("1e301")
    await driver.findElement(By.id("lado2")).click()
    await driver.findElement(By.id("lado2")).sendKeys("1e301")
    await driver.findElement(By.id("button")).click()
    assert.equal(await driver.findElement(By.id("area")).getAttribute('value'),"Los numeros son demasiado grandes para calcular")
    assert.equal(await driver.findElement(By.id("Perimetro")).getAttribute('value'),"Los numeros son demasiado grandes para calcular")
    await driver.findElement(By.id("lado1")).clear()
    await driver.findElement(By.id("lado2")).clear()
    await driver.findElement(By.id("lado3")).clear()
    await driver.findElement(By.id("lado1")).click()
    await driver.findElement(By.id("lado1")).sendKeys("1")
    await driver.findElement(By.id("lado2")).click()
    await driver.findElement(By.id("lado2")).sendKeys("2")
    await driver.findElement(By.id("lado3")).click()
    await driver.findElement(By.id("lado3")).sendKeys("3")
    await driver.findElement(By.id("button")).click()
    assert.equal(await driver.findElement(By.id("area")).getAttribute('value'),"El triangulo no existe")
    assert.equal(await driver.findElement(By.id("Perimetro")).getAttribute('value'),"El triangulo no existe")
    await driver.findElement(By.id("lado1")).clear()
    await driver.findElement(By.id("lado2")).clear()
    await driver.findElement(By.id("lado1")).click()
    await driver.findElement(By.id("lado1")).sendKeys("0")
    await driver.findElement(By.id("lado2")).click()
    await driver.findElement(By.id("lado2")).sendKeys("0")
    await driver.findElement(By.id("button")).click()
    assert.equal(await driver.findElement(By.id("area")).getAttribute('value'),"El triangulo no existe")
    assert.equal(await driver.findElement(By.id("Perimetro")).getAttribute('value'),"El triangulo no existe")
    await driver.close()
  })
})
